{"nodes":[{"id":"44eb446d-3775-4fe0-8f76-67f46913531e","name":"LoadDingTalkAccountMap","type":"n8n-nodes-base.mySql","position":[-600,720],"parameters":{"table":{"__rl":true,"mode":"list","value":"tfs_dingtalk_account_map","cachedResultName":"tfs_dingtalk_account_map"},"options":{},"operation":"select","returnAll":true},"credentials":{"mySql":{"id":"235","name":"MySQL account"}},"typeVersion":2.2},{"id":"25d2a3aa-af18-4ff2-af6c-c2bf6618a511","name":"ReceiveTfsPullRequestCreatedMessage","type":"n8n-nodes-base.webhook","position":[-860,720],"webhookId":"05a0f565-7a1e-44f2-956d-1c68982ce314","parameters":{"path":"pr-notify-template","options":{},"httpMethod":"POST"},"typeVersion":1},{"id":"70d69753-9408-4a99-85a4-9dc4486fc460","name":"BuildDingTalkWebHookData","type":"n8n-nodes-base.code","position":[-340,720],"parameters":{"jsCode":"// Code here will run only once, no matter how many input items there are.
// More info and help: https://docs.n8n.io/nodes/n8n-nodes-base.function

console.log("items:");
console.log(items);

var mapUserMobile = function (tfsAccount) {
  for(var i = 0; i < items.length; i++) {
    var map = items[i].json;
    if(tfsAccount.lastIndexOf(map.TfsAccount) != -1) {
      return map.DingTalkMobile;
    }
  }

  return null;
}
var mapUserName = function (tfsAccount) {
  for(var i = 0; i < items.length; i++) {
    var map = items[i].json;
    if(tfsAccount.lastIndexOf(map.TfsAccount) != -1) {
      return map.UserName;
    }
  }

  return null;
}

var tfsMessage = $node["ReceiveTfsPullRequestCreatedMessage"].json.body;
console.log("TFS Message:");
console.log(tfsMessage);

var output = {};
var atMobiles = [];
var isAtAll = false;
var atUsers = [];
var messageText = tfsMessage.message.markdown;
var prCreatorTfsDomainName = tfsMessage.resource.createdBy.uniqueName;
var prCreatorTfsDisplayName = tfsMessage.resource.createdBy.displayName;
var prCreatorDingTalkName = mapUserName(prCreatorTfsDomainName);

if (prCreatorDingTalkName !== null) {
  messageText = messageText.replace(prCreatorTfsDisplayName, prCreatorDingTalkName);
}

for (reviewer of tfsMessage.resource.reviewers) {
  console.log(reviewer.uniqueName);
  if(reviewer.uniqueName.lastIndexOf("团队") != -1) {
    //当@所有人时，消息内容中的手机号就不会被转义成用户名了，暂时不启用该功能
    isAtAll = true;
    continue;
  }

  var mobile = mapUserMobile(reviewer.uniqueName);

  if(mobile !== null) {
    atMobiles.push(mobile);
  }

  var userName = mapUserName(reviewer.uniqueName);

  if(userName !== null) {
    atUsers.push(userName);
  }
}

if(isAtAll) {
  atUsers.unshift("所有人");
  atMobiles = [];
} else {
  atUsers = atMobiles;
}

if (atUsers.length > 0) {
  messageText = messageText + "<br />请 @" + atUsers.join(" @") + " 评审";
}

output.isAtAll = isAtAll;
output.text = messageText;
output.atMobiles = atMobiles.join(", ");

console.log('Done:');
console.log(output);

return [{json: output}];"},"typeVersion":2},{"id":"dc6c235b-c1ac-4195-a404-e79c8ad1c8ef","name":"SendDingTalkMessageViaWebHook","type":"n8n-nodes-base.httpRequest","position":[-80,720],"parameters":{"url":"https://oapi.dingtalk.com/robot/send?access_token=4d684f0389cd4c24997c815344890faafeb9d300c25edf5e31151662a87e77c7","options":{},"requestMethod":"POST","jsonParameters":true,"bodyParametersJson":"={
