  return i > 0 && parts[i - 1] === '-e';
})
  .reduce((accu, opt) => {
  if (!opt.includes('=')) {
    return accu;
  }

  const key = opt.split('=')[0];
  const val = opt.split('=')[1];

  accu[key] = clean(val);
  return accu;
}, {});

